sequence.script:

var limit = mc.getProperty('uri.var.limit');
var offset = mc.getProperty('uri.var.offset');
var paidOnAfter = mc.getProperty('uri.var.paidOnAfter');
var apiParameters = mc.getProperty('uri.var.apiParameters');
if (limit != null && limit != "") {
   apiParameters += "limit=" + limit + '&';
}
if (offset != null && offset != "") {
   apiParameters += "offset=" + offset + '&';
}
if (paidOnAfter != null && paidOnAfter != "") {
   apiParameters += "paid_on_after=" + paidOnAfter + '&';
}
if (apiParameters != "") {
   apiParameters = '?' + apiParameters.substring(0, apiParameters.length - 1);
   mc.setProperty('uri.var.apiParameters', apiParameters);
}
